import { verifyingToken } from '../utils/jwtToken.js';

const checkAuth = {
  /**
   * @property {Function} verifyUser verifying the user
   * @returns {token}
   */
  verifyUser: (req, res, next) => {
    const token = req.headers.token;
    if (!token) {
      return res.status(400).send({ error: 'no token provided' });
    }
    if (token) {
        try {
          const user = verifyingToken(token);
          req.user = user;
          next();
        } catch (error) {
          return res.status(401).send({ error: 'Invalid Token' });
        }
      
    }
  },
};

export default checkAuth;

import Joi from 'joi';

export function userSignInValidate(req, res, next) {
  const userSignIn = Joi.object({
    email: Joi.string().min(8).required().email()
      .trim(),
    password: Joi.string().min(6).required()
      .trim()

  });
  const result = userSignIn.validate(req.body);
  if (result.error) return res.status(400).json({ Message: result.error.details[0].message });
  next();
}

import Joi from 'joi';

export function userSignUpValidate(req, res, next) {
  const userSignUp = Joi.object({
    name: Joi.string().min(4).required(),
    email: Joi.string().min(8).required().email()
      .trim(),
    password: Joi.string().min(6).required()
      .trim(),
    confirmPassword: Joi.string().min(6).required().trim()

  });
  const result = userSignUp.validate(req.body);
  if (result.error) return res.status(400).json({ Message: result.error.details[0].message });
  next();
}

import Joi from 'joi';

export function todoValidator(req, res, next) {
  const todoValidate = Joi.object({
    title: Joi.string().min(4).required().trim(),
    description: Joi.string().min(4).trim(),
    priority: Joi.string().required().trim()

  });
  const result = todoValidate.validate(req.body);
  if (result.error) return res.status(400).json({ Message: result.error.details[0].message });
  next();
}

import mongoose from 'mongoose'

const connectDB = async () => {
  try {
    const conn = await mongoose.connect(process.env.MONGO_URI,{
      useUnifiedTopology:true,
      useNewUrlParser: true,
      useCreateIndex: true
    });
    console.log(`MongoDB connected: ${conn.connection.host}`)
  } catch (error) {
    console.error(`Error: ${error.message}`);
    process.exit(1)
  }
}

export default connectDB
